

const windows = require('./windows');
const linux = require('./linux');
const {getCmdArgs} = require("../utils");
const {isWindows} = require("../utils");

const target = isWindows() ? windows : linux;

exports.kill = () => {
  const [pid] = getCmdArgs();
  if(!pid){
    return console.log('未找到pid');
  }
  target.kill(pid);
};

exports.killPort = () => {
  const [port] = getCmdArgs();
  if(!port){
    return console.log('未找到port');
  }
  target.killByPort(port);
};

