

const {fork} = require('child_process');
const path = require('path');
const {getCmdArgs} = require("./utils");

module.exports = () => {
  const [filePath] = getCmdArgs();
  if(!filePath){
    return console.log('未找到指定js路径');
  }
  fork(path.resolve(process.cwd(),filePath),{
    detached:true
  });
  process.exit();
};

